function c() {
  isFlex
    ? ((imgTag.src = "/assets/icons/bars-solid.svg"),
      (divRight.style.display = "none"))
    : ((divRight.style.display = "flex"),
      (imgTag.src = "/assets/icons/xmark-solid.svg")),
    (isFlex = !isFlex);
}
const divRight = document.querySelector(".right"),
  imgTag = document.querySelector("#nav_button img");
let isFlex = "flex" === divRight.style.display;
window.getComputedStyle(divRight).width.replace("px", "") > 600 &&
  (divRight.style.display = "flex"),
  c(),
  (isFlex = "flex" === divRight.style.display),
  document
    .querySelector("#nav_button button")
    .addEventListener("click", function () {
      c();
    }),
  window.addEventListener("resize", function () {
    c();
  });
