function isAndroid(){return/Android/i.test(navigator.userAgent)}function applyGyroscopeEffect(){window.addEventListener("deviceorientation",e=>{const t=e.beta,o=e.gamma,r=o/45,n=t/25,d=10*r,i=10*n;div.style.transform=`perspective(1000px) rotateY(${25*r}deg) rotateX(${5*n}deg) scale3d(1, 1, 1)`,div.style.boxShadow=`${d}px ${i}px 20px 0px #000000d6`})}const div=document.querySelector(".tilt");window.innerWidth<600||isAndroid()?applyGyroscopeEffect():div.addEventListener("mousemove",e=>{const{top:t,bottom:o,left:r,right:n}=e.currentTarget.getBoundingClientRect(),d=(n-r)/2,i=(o-t)/2,a=e.clientX,c=e.clientY,s=(a-r-d)/d,p=(c-t-i)/i,l=10*s,g=10*p;e.currentTarget.style.transform=`perspective(1000px) rotateY(${5*s}deg) rotateX(${5*p}deg) scale3d(1, 1, 1)`,e.currentTarget.style.boxShadow=`${l}px ${g}px 20px 0px #000000d6`});